import * as React from "react";

const divStyle = {
    color: 'blue',
    backgroundColor:"#DD99BB",
    margin : '10px',
    padding : '10px',
};

export default class NewsItem extends React.Component {
    formatTime(time){
        const dt = new Date(time);
        const ds = (dt.toDateString());
        const h = (dt.getHours());
        const min = (dt.getMinutes() ? dt.getMinutes() > 9 : '0' + dt.getMinutes());
        return `[${ds} ${h}:${min}]`;
    }

    render() {
        return <a rel="noreferrer" href={this.props.article.url} target="_blank" style={divStyle}>
            <h1>Title : {this.props.article.title}</h1>
            <h5>score : {this.props.article.score}, published on {this.formatTime(this.props.article.time)} by <i>{this.props.article.by}</i>, karma {this.props.article.author.karma}</h5>
        </a>
    }
}
