import * as React from "react";

const divStyle = {
    color: 'blue',
    backgroundColor:"#DD99BB",
    margin : '10px',
    padding : '10px',
};

export default class NewsItem extends React.Component {
    formatTime(time){
        const dt = new Date(time);
        const y = dt.getFullYear();
        const m = (dt.getMonth() + 1);
        const d = (dt.getDate()); //day of month
        const w = dt.toDateString().substring(0, 3); //day of week enum, either Mon, Tue, Wed, Thu, Fri, Sat, Sun
        const h = (dt.getHours());
        const min = (dt.getMinutes());
        return `[${y}-${m}-${d} ${w} ${h}:${min}]`;
    }

    render() {
        return <div style={divStyle}>
            <h1>Title : {this.props.article.title}</h1>
            <h5>score : {this.props.article.score}, published on {this.formatTime(this.props.article.time)} by <i>{this.props.article.by}</i>, karma {this.props.article.author.karma}</h5>
            <a rel="noreferrer" href={this.props.article.url} target="_blank">{this.props.article.url}</a>
        </div>
    }
}
